function(doc) {
  var docs = [];

  if (doc !== null && doc.hasField('ticker')) {
    var ticker = doc.getFirstFieldValue('ticker');
    logger.info('Getting price history for ' + ticker);
    if (ticker !== null) {

      var entityUtils = Packages.org.apache.http.util.EntityUtils;
      var httpClient = org.apache.http.impl.client.HttpClients.createDefault();
      var url = 'https://api.iextrading.com/1.0/stock/' + ticker + '/chart';
      var httpGet = new org.apache.http.client.methods.HttpGet(url);

      var response = entityUtils.toString(httpClient.execute(httpGet).getEntity());
      var json_response = JSON.parse(response);
      for (var i = 0; i < json_response.length; i++) {
        var price_record = json_response[i];

        // reliable ID for recrawl, e.g., to support data correct from IEX
        var newDoc = new com.lucidworks.apollo.common.pipeline.PipelineDocument('historical_price_' + ticker + '_' + price_record.date);

        newDoc.addField('_lw_data_source_s', doc.getFirstFieldValue('_lw_data_source_s'));
        newDoc.addField('ticker', ticker);
        newDoc.addField('date', price_record.date);
        newDoc.addField('open', price_record.open);
        newDoc.addField('high', price_record.high);
        newDoc.addField('low', price_record.low);
        newDoc.addField('close', price_record.close);
        newDoc.addField('volume', price_record.volume);
        newDoc.addField('unadjusted_volume', price_record.unadjustedVolume);
        newDoc.addField('change', price_record.change);
        newDoc.addField('change_percent', price_record.changePercent);
        newDoc.addField('vwap', price_record.vwap);
        docs.push(newDoc);
      }
    }
  }
  // IEX doesn't like to be hit hard, sleep for a bit to throttle
  java.lang.Thread.sleep(200);
  return docs;
}

